import React from 'react';
import {useContext} from 'react';
import CurrentUserContext from '../contexts/CurrentUserContext';

function Card({card, onCardLike, onCardDelete, onCardClick}) {
    const currentUser = useContext(CurrentUserContext);
    const isOwn = card.owner._id === currentUser._id;
    const isLiked = card.likes.some(i => i._id === currentUser._id);
    const cardLikeButtonClassName = ( 
        `card__like-button ${isLiked && 'card__like-button_active'}` 
      );

    function handleCardClick() {
        card.onCardClick(card);
    }

    function handleDeleteClick() {
        onCardDelete(card);
    }

    function handleCardClick() {
        onCardClick(card);
    }

    return (
        <article className="card">
            <button className="card__trash pointer" type="button" />
            <img className="card__image" alt={card.name} src={card.link} onClick={handleCardClick} />
            <div className="card__caption">
                <h2 className="card__title">{card.name}</h2>
                <div className="cards__item-likes">
                    <button className="card__like-button pointer" type="button" />
                    <p className="card__likes-counter">{card.likes.length}</p>
                </div>
            </div>
        </article>
    )
}

export default Card;
