import React from 'react';
import { useState, useEffect } from 'react'
import api from '../utils/Api.js';

import Header from './Header.js'
import Footer from './Footer';
import Main from './Main.js'
import PopupWithForm from './PopupWithForm.js'
import ImagePopup from './ImagePopup.js';
import CurrentUserContext from '../contexts/CurrentUserContext';
import EditProfilePopup from './EditProfilePopup.js';
import EditAvatarPopup from './EditAvatarPopup.js'

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
  const [selectedCard, setSelectedCard] = React.useState({});
  const [currentUser, setCurrentUser] = useState({});
  const [cards, setCards] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    api.getUserInfo()
      .then(profileInfo => setCurrentUser(profileInfo))
      .catch(err => console.log(err))

    api.getInitialCards().then(cardsData => {
      setCards(cardsData.map((data) => ({
        _id: data._id,
        name: data.name,
        link: data.link,
        likes: data.likes,
        owner: data.owner
      })))
    })
      .catch(err => console.log(err))
  }, []);


  function closeAllPopups() {
    setIsEditProfilePopupOpen(false)
    setIsAddPlacePopupOpen(false)
    setIsEditAvatarPopupOpen(false)
    setSelectedCard({});
  }

  function handleCardLike(card) {
    const isLiked = card.likes.some(user => user._id === currentUser._id);
    api.putLike(card._id, !isLiked)
      .then(newCard => setCards((
        state) => state.map(
          item => item._id === card._id ? newCard : item)))
      .catch(err => console.log(err));
  }

  function handleCardDelete(card) {
    api.deleteCard(card._id)
      .then(() => setCards(
        state => state.filter(
          item => item._id !== card._id)))
      .catch(err => console.log(err));
  }

  function handleUpdateUser(inputData) {
    setIsLoading(true);
    api.setUserInfo(inputData)
      .then(data => {
        setCurrentUser(data);
        closeAllPopups();
      })
      .catch(err => console.log(err))
      .finally(() => setIsLoading(false));
  }

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="page">
        <div className="page__container">
          <Header />
          <Main
            onEditProfile={setIsEditProfilePopupOpen}
            onEditAvatar={setIsEditAvatarPopupOpen}
            onAddPlace={setIsAddPlacePopupOpen}
            onCardClick={setSelectedCard}
            onCardLike={handleCardLike}
            onCardDelete={handleCardDelete}
            cards={cards}
          />
          <Footer />
          <ImagePopup card={selectedCard} onClose={closeAllPopups} />
          <PopupWithForm
            name="delete-confirmation"
            title="Вы уверены?"
            buttonText="Да">
          </PopupWithForm>
          <EditAvatarPopup />

          {/* <PopupWithForm
          name="change-avatar"
          title="Обновить аватар"
          isOpen={isEditAvatarPopupOpen}
          onClose={closeAllPopups}>
          <label className="form popup__form" name="form_change-avatar">
            <input
              className="form__input form__input_text_info"
              name="link"
              id="url-avatar-image"
              type="url"
              placeholder="Ссылка на новый аватар"
              required />
            <span className="url-avatar-image-error form__input-error"></span>
          </label>
        </PopupWithForm> */}

          <EditProfilePopup
            isOpen={isEditProfilePopupOpen}
            onClose={closeAllPopups}
            onLoading={isLoading}
            onUpdateUser={handleUpdateUser}
          />

          <PopupWithForm
            name="add"
            title="Новое меcто"
            buttonText="Создать"
            isOpen={isAddPlacePopupOpen}
            onClose={closeAllPopups}>
            <label className="form popup__form">
              <input
                className="form__input form__input_info_title"
                type="text"
                name="name"
                id="place-name"
                placeholder="Название"
                minLength="2"
                maxLength="30"
                required />
              <span className="place-name-error form__input-error"></span>
            </label>
            <label className="form popup__form">
              <input
                className="form__input form__input_info_link"
                type="url"
                name="link"
                id="link-url"
                placeholder="Ссылка на картинку"
                required />
              <span className="link-url-error form__input-error"></span>
            </label>
          </PopupWithForm>
        </div>
      </div>
    </CurrentUserContext.Provider>
  );

}

export default App;
